mapper.update:
UPDATE  tbl_board
SET     title = #{title}, name = #{name},
		content = #{content},
		modidate = CURRENT_TIMESTAMP
WHERE   pno = #{pNo}
AND 	pass = #{pass}

mapper.delete:
DELETE
FROM 	tbl_board
WHERE 	pno = #{pNo}
AND 	pass = #{pass}

mapper.update:
UPDATE 	tbl_board
SET 	ccnt = ccnt + #{amount}
WHERE 	pno = #{pNo}
